create table candidato (id_candidato int8 generated by default as identity, cargo1candidato varchar(255), cargo2candidato varchar(255), cargo3candidato varchar(255), cod_identificacao varchar(255), email_candidato varchar(255), estado_civil_candidato varchar(255), idioma_candidato varchar(255), nacionalidade_candidato varchar(255), nascimento_candidato timestamp, nome_candidato varchar(255), outro_idioma_candidato varchar(255), senha_candidato varchar(255), primary key (id_candidato));
create table empresa (id_empresa int8 generated by default as identity, cep_empresa varchar(255), cnpj_empresa varchar(255), linkedin_empresa varchar(255), nome_empresa varchar(255), ramo_empresa varchar(255), telefone_empresa varchar(255), primary key (id_empresa));
create table recrutador (id_recrutador int8 generated by default as identity, cargo_recrutador varchar(255), cod_empresa int4, departamento_recrutador varchar(255), email_recrutador varchar(255), empresa_recrutador varchar(255), nome_recrutador varchar(255), outro_contato_recrutador varchar(255), senha_recrutador varchar(255), tel_recrutador varchar(255), primary key (id_recrutador));
create table vaga (cod_vaga int8 generated by default as identity, cargo_vaga varchar(255), descricao_vaga varchar(255), localidade_vaga varchar(255), nivel_instrucao_vaga varchar(255), nome_empresa_vaga varchar(255), regime_contratacao_vaga varchar(255), salario_vaga float8, vaga_destaque varchar(255), vaga_pcd varchar(255), primary key (cod_vaga));
